mapper.update:
UPDATE email_verification_token
SET used_at = #{usedAt}
WHERE email_verification_token_id = #{emailVerificationTokenId}
  AND used_at IS NULL

mapper.select:
SELECT COUNT(1)
FROM email_verification_token
WHERE email = #{email}
  AND purpose = #{purpose}
  AND used_at IS NOT NULL
  AND expires_at > #{now}
