mapper.select:
SELECT




        AND p.is_active = #{isActive}


        AND(
            p.title LIKE CONCAT('%', #{searchKeyword}, '%') OR
            f.flight_number LIKE CONCAT('%', #{searchKeyword}, '%') OR
            al.airline_name LIKE CONCAT('%', #{searchKeyword}, '%')
        )


ORDER BY p.display_order ASC, p.created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(p.promotion_id) FROM promotion p LEFT JOIN flight f ON p.flight_id = f.flight_id LEFT JOIN airline al ON f.airline_id = al.airline_id
AND p.is_active = #{isActive}
AND ( p.title LIKE CONCAT('%', #{searchKeyword}, '%') OR f.flight_number LIKE CONCAT('%', #{searchKeyword}, '%') OR al.airline_name LIKE CONCAT('%', #{searchKeyword}, '%') )
