mapper.update:
UPDATE flight_info
SET

    first_class_seat = first_class_seat + #{count}
    business_class_seat = business_class_seat + #{count}
    economy_class_seat = economy_class_seat + #{count}
    flight_info_id = flight_info_id

WHERE flight_id = #{flightId}

UPDATE reservation
SET status = #{status}
WHERE reservation_id = #{reservationId}
