mapper.select:
SELECT fs.flight_seat_id AS flightSeatId, fi.flight_id AS flightId,
aseat.seat_no AS seatNo, aseat.row_no AS rowNo, aseat.col_no AS colNo, aseat.cabin_class_code AS cabinClassCode,
/* 만료된 HOLD는 즉시 AVAILABLE로 표시 */ CASE WHEN fs.seat_status = 'HOLD' AND fs.hold_expires_at IS NOT NULL AND fs.hold_expires_at < NOW() THEN 'AVAILABLE' ELSE COALESCE(fs.seat_status, 'AVAILABLE') END AS seatStatus,
/* 만료된 HOLD면 holdExpiresAt도 NULL로 내려줌 */ CASE WHEN fs.seat_status = 'HOLD' AND fs.hold_expires_at IS NOT NULL AND fs.hold_expires_at < NOW() THEN NULL ELSE fs.hold_expires_at END AS holdExpiresAt FROM flight_info fi JOIN aircraft_seat aseat ON aseat.aircraft_id = fi.aircraft_id LEFT JOIN flight_seat fs ON fs.flight_id = fi.flight_id AND fs.aircraft_seat_id = aseat.airplane_seat_id WHERE fi.flight_id = #{flightId} ORDER BY aseat.cabin_class_code, aseat.row_no, aseat.col_no

SELECT rs.flight_id
FROM reservation_segment rs
WHERE rs.reservation_segment_id = #{reservationSegmentId}
  AND rs.reservation_id = #{reservationId}

SELECT status
FROM reservation
WHERE reservation_id = #{reservationId}

SELECT expired_at
FROM reservation
WHERE reservation_id = #{reservationId}

SELECT COUNT(*)
FROM passenger
WHERE reservation_id = #{reservationId}
  AND passenger_id = #{passengerId}

SELECT ps.flight_seat_id
FROM passenger_seat ps
WHERE ps.reservation_segment_id = #{reservationSegmentId}
  AND ps.passenger_id = #{passengerId}
    FOR UPDATE

mapper.delete:
DELETE FROM passenger_seat
WHERE reservation_segment_id = #{reservationSegmentId}
  AND passenger_id = #{passengerId}
