mapper.insert:
INSERT INTO user_profile (
    user_id,
    name
) VALUES (
             #{userId},
             #{name}
         )

mapper.update:
UPDATE user_profile
name = #{name}, kr_first_name = #{krFirstName}, kr_last_name = #{krLastName}, birth = #{birth}, email = #{email}, phone_number = #{phoneNumber}, passport_no = #{passportNo}, passport_expiry_date = #{passportExpiryDate}, passport_issue_country = #{passportIssueCountry}, country = #{country}, gender = #{gender}, first_name = #{firstName}, last_name = #{lastName},
WHERE user_id = #{userId}
